import React, { Component } from 'react';
import {
    BrowserRouter as Router,
  Route,
  Link} from 'react-router-dom';

const imgPath = process.env.PUBLIC_URL + '/assets/images/';

class Header extends Component {
    constructor(props) {
        super(props)
        this.state = {
            navActive: false,
            home: true,
            share: false,
            roads: false,
            ant: false,
            about: false,
            contact: false
        }
        this.handleNav = this.handleNav.bind(this)
    }

    handleNav() {
        this.setState({
            navActive: !this.state.navActive
        })
    }

    turnActiveOn(state) {
        this.setState({
            home: false,
            share: false,
            roads: false,
            ant: false,
            about: false,
            contact: false
        });
        this.setState({
            state: true
        });
        this.handleNav();
    }

    render() {
        return (
            <div className="main-header">
                <div className="center-header">
                    <a href="/index.html">
                        <img src={`${imgPath}logo.svg`} alt="logo"/>
                        <h1>acola.me</h1>
                    </a>
                    <div id="nav-icon3" className={this.state.navActive ? 'open' : '' } onClick={this.handleNav}>
                        <span></span>
                        <span></span>
                        <span></span>
                        <span></span>
                    </div>
                    <nav className="main-nav">
                        <ul className={this.state.navActive ? 'active' : '' }>
                            <li>
                                <Link onClick={() => { this.turnActiveOn(this.state.home) }} className={this.state.home ? "current-menu-item" : "" } to='/'>Inicio</Link>
                            </li>
                            <li>
                                <Link onClick={() => { this.turnActiveOn(this.state.share) }} className={this.state.share ? "current-menu-item" : "" } to='/compartir'>Compartir Viajes</Link>
                            </li>
                            <li>
                                <Link className={this.state.roads ? "current-menu-item" : "" } to='/vias'>Estado de v√≠as</Link>
                            </li>
                            <li>
                                <Link className={this.state.ant ? "current-menu-item" : "" } to='/ant'>Obligaciones ANT</Link>
                            </li>
                            <li>
                                <Link className={this.state.about ? "current-menu-item" : "" } to='/acercade'>Acerca De</Link>
                            </li>
                            <li>
                                <Link className={this.state.contact ? "current-menu-item" : "" } to='/conctacto'>Contacto</Link>
                            </li>
                        </ul>
                    </nav>
                </div>  
            </div>
        )
    }
}

export default Header;
