import React, { Component } from 'react'

class InputPass extends Component {
    constructor(props) {
      super(props);
      this.state = {
        inputValue: '',
        isPasswordShow: false
      };
    }

    handlePassword() {
      this.setState({
        isPasswordShow: !this.state.isPasswordShow
      })
    }

    render() {
      return (
        //...
        <input id="post-password" value={this.state.inputValue} onChange={evt => this.updateInputValue(evt)} type="password" placeholder="clave" />
        //...
      );
    }

    updateInputValue(evt) {
      this.setState({
        inputValue: evt.target.value
      });
    }
  }

  export default InputPass
